task.replace:
  OPTIONAL{
  SELECT  ?domainC (COUNT(*) AS ?nroSubConcepts)
  WHERE{
    ?subConcept rdfs:subClassOf+ ?domainC .
	FILTER NOT EXISTS {
	  ?subConcept rdfs:subClassOf|owl:equivalentClass ?someYsoC .
	  ?someYsoC rdf:type ?someYsoCType .
      {?someYsoCType rdfs:subClassOf+ owl:Class .
        FILTER (strstarts(str(?someYsoCType),"http://www.yso.fi/onto/yso")) .}}
  } GROUP BY ?domainC
  }

query.sparql:
# 1-same-pref.rq #prefLabel same SELECT DISTINCT ('1.1' AS ?queryType) ?domainC ?nroSubConcepts ?domainCtypes ?domainCpref ?relationType ?interface ?ysoC ?ysoCtypes ?ysoCpref ?changeDescription WHERE { GRAPH ?anyNamedGraph { {*yso-concept-selector-query*} . FILTER NOT EXISTS {?ysoC owl:deprecated true .} . FILTER NOT EXISTS {?ysoC rdf:type ysometa:Hierarchy .} } # Include only YSO Concepts that exists in the old YSO FILTER NOT EXISTS { ?ysoC rdf:type ?anyTypeX } . ?domainC skos:prefLabel ?ysoCpref . {*domain-concept-selector-query*}. BIND( "" AS ?interface) BIND(concat("Uudella YSO käsitteellä '",?ysoCpref, "' (yso:",substr(str(?ysoC),28) ,") on sama prefLabel kuin erikoisontologian käsitteellä") AS ?changeDescription)
*count-domainC-subClasses-as-nroSubConcepts* BIND ("Ei suhdetta" AS ?relationType)
} ORDER BY ?domainC

# 2-diff-broad-pref.rq
SELECT DISTINCT ('2.1' AS ?queryType) ?domainC ?nroSubConcepts ?domainCtypes ?domainCpref ?relationType ?interface ?ysoC ?ysoCtypes ?ysoCpref ?changeDescription 
WHERE {
   
  GRAPH ?anyNamedGraph {
    ?ysoC skos:prefLabel ?ysoCnewPref .
    FILTER(lang(?ysoCnewPref) = '*lang*') .
    #FILTER NOT EXISTS {?ysoC skos:prefLabel ?ysoCpref} .
    FILTER NOT EXISTS {{?ysoC owl:deprecated true} UNION {?ysoC rdf:type ysometa:Hierarchy}} . 
  }
  {*yso-concept-selector-query*}
  FILTER(?ysoCnewPref != ?ysoCpref) .
  FILTER NOT EXISTS { GRAPH ?yso {?ysoC owl:deprecated true } } . 

  ?domainC skos:prefLabel ?ysoCnewPref
  {*domain-concept-selector-query*}
  FILTER NOT EXISTS { ?domainC owl:equivalentClass ?ysoC } . 
  BIND( "" AS ?interface)
  BIND(concat("'",?ysoCpref, "' (yso:",substr(str(?ysoC),28) ,") käsitteen uusi prefLabel on sama kuin erikoisontologian käsitteellä") AS ?changeDescription) .
  
  *count-domainC-subClasses-as-nroSubConcepts*
  BIND ("Ei suhdetta" AS ?relationType)
} ORDER BY ?domainC

# 2-diff-broad-pref.rq SELECT DISTINCT ('2.2' AS ?queryType) ?domainC ?nroSubConcepts ?domainCtypes ?domainCpref ?relationType ?interface ?ysoC ?ysoCtypes ?ysoCpref ?changeDescription WHERE { {*domain-concept-selector-query*} ?domainC rdfs:subClassOf|owl:equivalentClass|owl:equivalentClass/rdfs:subClassOf ?ysoCinterface . ?ysoCinterface rdf:type ?ysoCinterfaceType . {?ysoCinterfaceType rdfs:subClassOf+ owl:Class . FILTER (strstarts(str(?ysoCinterfaceType),"http://www.yso.fi/onto/yso")) .} ?ysoCinterface rdfs:subClassOf* ?ysoC .
# do not report twice if concept is connected multiple ways, only direct change FILTER NOT EXISTS {?domainC rdfs:subClassOf ?ysoC . ?domainC owl:equivalentClass ?ysoCinterface . ?ysoCinterface rdfs:subClassOf ?ysoC}
{*yso-concept-selector-query*} GRAPH ?anyNamedGraph { ?ysoC skos:prefLabel ?ysoCnewPref . FILTER(lang(?ysoCnewPref) = '*lang*') . #FILTER NOT EXISTS {?ysoC skos:prefLabel ?ysoCpref} . FILTER NOT EXISTS {{?ysoC owl:deprecated true} UNION {?ysoC rdf:type ysometa:Hierarchy}} . } FILTER(?ysoCnewPref != ?ysoCpref) . FILTER NOT EXISTS { GRAPH ?yso {?ysoC owl:deprecated true } } .
OPTIONAL {?domainC ?directRelation ?ysoC} . BIND (IF(?ysoCinterface = ?ysoC, "kyllä" ,"ei") AS ?interface) BIND (IF(?ysoCinterface != ?ysoC, "rdfs:subClassOf+" , IF(?directRelation = rdfs:subClassOf , "rdfs:subClassOf", IF(?directRelation = owl:equivalentClass, "owl:equivalentClass", "owl:equivalentClass/rdfs:subClassOf" # Because ysoC is ysoCInterface, but ?directRelation is not rdfs:subClassOf or owl:equivalentClass ) ) ) AS ?relationType). BIND(concat("'",?ysoCpref, "' (yso:",substr(str(?ysoC),28) ,") käsitteen prefLabel on muuttunut -> '",?ysoCnewPref,"'") AS ?changeDescription) .
*count-domainC-subClasses-as-nroSubConcepts*
} ORDER BY ?domainC

#  	3-diff-broader.rq
SELECT DISTINCT ?queryType ?domainC ?nroSubConcepts ?domainCtypes ?domainCpref ?relationType ?interface ?ysoC ?ysoCtypes ?ysoCpref ?changeDescription 
WHERE{
{
SELECT DISTINCT ('3.1' AS ?queryType) ?domainC ?domainCtypes ?domainCpref ?relationType ?interface ?ysoC ?ysoCtypes ?ysoCpref ?changeDescription 
WHERE {
  {*domain-concept-selector-query*}
  ?domainC rdfs:subClassOf|owl:equivalentClass ?ysoCinterface .
  ?ysoCinterface rdf:type ?ysoCinterfaceType .
  {?ysoCinterfaceType rdfs:subClassOf+ owl:Class .
  FILTER (strstarts(str(?ysoCinterfaceType),"http://www.yso.fi/onto/yso")) .}
  
  GRAPH ?anyNamedGraph {
    ?ysoCinterface rdfs:subClassOf* ?ysoC .
    
    ?ysoC rdfs:subClassOf ?ysoC2 .
    ?ysoC2 skos:prefLabel ?ysoC2pref .
    FILTER(lang(?ysoC2pref) = '*lang*')
  }
  ?ysoCinterface rdfs:subClassOf* ?ysoC .
  
  # do not report twice if concept is connected multiple ways, only direct change
  FILTER NOT EXISTS {?domainC rdfs:subClassOf ?ysoC .
	                 ?domainC owl:equivalentClass ?ysoCinterface .
                     ?ysoCinterface rdfs:subClassOf ?ysoC} 
  
  FILTER NOT EXISTS { ?ysoCinterface rdfs:subClassOf* ?ysoC .
                      ?ysoC rdfs:subClassOf ?ysoC2 } 
					  
					  
					  
  {*yso-concept-selector-query*} .
  
  FILTER NOT EXISTS { GRAPH ?yso { {?ysoC owl:deprecated true . } UNION 
                                   {?ysoC rdf:type  ysometa:DeprecatedConcept , ysometa:DeprecatedAggregateConcept . } UNION
                                   {?ysoC2 owl:deprecated true} UNION
                                   {?ysoC2 rdf:type  ysometa:DeprecatedConcept , ysometa:DeprecatedAggregateConcept . }
                                   }}

  OPTIONAL {?domainC ?directRelation ?ysoC} .
  BIND (IF(?ysoCinterface = ?ysoC, "kyllä" ,"ei") AS ?interface)
  BIND (IF(?ysoCinterface != ?ysoC, 
            "rdfs:subClassOf+" ,
            IF(?directRelation = rdfs:subClassOf ,
               "rdfs:subClassOf",
               IF(?directRelation = owl:equivalentClass,
                  "owl:equivalentClass",
                  "owl:equivalentClass/rdfs:subClassOf" # Because ysoC is ysoCInterface, but ?directRelation is not rdfs:subClassOf or owl:equivalentClass
                  )
              )
          ) 
	   AS ?relationType).
  
  BIND(concat("'",?ysoCpref, "' (yso:",substr(str(?ysoC),28) ,") käsitteellä on uusi yläkäsite  '",?ysoC2pref,"' (yso:",substr(str(?ysoC2),28) ,")") AS ?changeDescription)
}
  
} UNION {

{
SELECT DISTINCT ('3.2' AS ?queryType) ?domainC ?domainCtypes ?domainCpref ?relationType ?interface ?ysoC ?ysoCtypes ?ysoCpref ?changeDescription 
WHERE {
  {*domain-concept-selector-query*}
  
  # ?ysoCinterface YSO Concept that is derectly connected to domain ontology (ie. equivalent or direct parent)
  ?domainC rdfs:subClassOf|owl:equivalentClass ?ysoCinterface .
  ?ysoCinterface rdf:type ?ysoCinterfaceType .
  ?ysoCinterfaceType rdfs:subClassOf+ owl:Class .
  FILTER (strstarts(str(?ysoCinterfaceType),"http://www.yso.fi/onto/yso")) .

  {?ysoCinterface rdfs:subClassOf* ?ysoC .
  ?ysoC rdfs:subClassOf ?ysoC2 .}
  
  # do not report twice if concept is connected multiple ways, only direct change
  FILTER NOT EXISTS {?domainC rdfs:subClassOf ?ysoC .
	                 ?domainC owl:equivalentClass ?ysoCinterface .
                     ?ysoCinterface rdfs:subClassOf ?ysoC} 
  
  FILTER NOT EXISTS { GRAPH ?yso { ?ysoC rdfs:subClassOf ?ysoC2 } }
  
  {*yso-concept-selector-query*} 
  
  {?ysoC2 skos:prefLabel ?ysoC2pref .
  FILTER(lang(?ysoC2pref) = '*lang*')}
  
  FILTER NOT EXISTS { GRAPH ?yso { {?ysoC owl:deprecated true . } UNION 
                                   {?ysoC rdf:type  ysometa:DeprecatedConcept , ysometa:DeprecatedAggregateConcept . } UNION
                                   {?ysoC2 owl:deprecated true} UNION
                                   {?ysoC2 rdf:type  ysometa:DeprecatedConcept , ysometa:DeprecatedAggregateConcept . }
                                   }}
  
  OPTIONAL {?domainC ?directRelation ?ysoC} .
  BIND (IF(?ysoCinterface = ?ysoC, "kyllä" ,"ei") AS ?interface)
  BIND (IF(?ysoCinterface != ?ysoC, 
            "rdfs:subClassOf+" ,
            IF(?directRelation = rdfs:subClassOf ,
               "rdfs:subClassOf",
               IF(?directRelation = owl:equivalentClass,
                  "owl:equivalentClass",
                  "owl:equivalentClass/rdfs:subClassOf" # Because ysoC is ysoCInterface, but ?directRelation is not rdfs:subClassOf or owl:equivalentClass
                  )
              )
          ) 
	   AS ?relationType).
  
  BIND(concat("'",?ysoCpref, "' (yso:",substr(str(?ysoC),28) ,") käsitteeltä on poistunut yläkäsite  '",?ysoC2pref,"' (yso:",substr(str(?ysoC2),28) ,") uudessa YSOssa") AS ?changeDescription)
  }
}
  
}UNION {

{
SELECT DISTINCT ('3.3' AS ?queryType) ?domainC ?domainCtypes ?domainCpref ?relationType ?interface ?ysoC ?ysoCtypes ?ysoCpref ?changeDescription 
WHERE {
  {*domain-concept-selector-query*}
  
  # ?ysoCinterface YSO Concept that is derectly connected to domain ontology (ie. equivalent or direct parent)
  ?domainC rdfs:subClassOf|owl:equivalentClass ?ysoCinterface .
  ?ysoCinterface rdf:type ?ysoCinterfaceType .
  ?ysoCinterfaceType rdfs:subClassOf+ owl:Class .
  FILTER (strstarts(str(?ysoCinterfaceType),"http://www.yso.fi/onto/yso")) .

  {?ysoCinterface rdfs:subClassOf* ?ysoC .
  ?ysoC rdfs:subClassOf? ?ysoC2 .} .
  
  # do not report twice if concept is connected multiple ways, only direct change
  FILTER NOT EXISTS {?domainC rdfs:subClassOf ?ysoC .
	                 ?domainC owl:equivalentClass ?ysoCinterface .
                     ?ysoCinterface rdfs:subClassOf ?ysoC} 
  
  GRAPH ?yso { {?ysoC2 rdf:type ysometa:DeprecatedConcept ,
                                ysometa:DeprecatedAggregateConcept .
                } UNION {
				?ysoC2 owl:deprecated true} } .
				
  #report deprecated concepts only through their superordinate concepts
  #unless the deprecated concept is directly connected
  FILTER(!(?ysoCinterface != ?ysoC && ?ysoC = ?ysoC2))
  
  {*yso-concept-selector-query*} 
  
  {?ysoC2 skos:prefLabel ?ysoC2pref .
  FILTER(lang(?ysoC2pref) = '*lang*')}
  
  
  OPTIONAL {?domainC ?directRelation ?ysoC} .
  BIND (IF(?ysoCinterface = ?ysoC, "kyllä" ,"ei") AS ?interface)
  BIND (IF(?ysoCinterface != ?ysoC, 
            "rdfs:subClassOf+" ,
            IF(?directRelation = rdfs:subClassOf ,
               "rdfs:subClassOf",
               IF(?directRelation = owl:equivalentClass,
                  "owl:equivalentClass",
                  "owl:equivalentClass/rdfs:subClassOf" # Because ysoC is ysoCInterface, but ?directRelation is not rdfs:subClassOf or owl:equivalentClass
                  )
              )
          ) 
	   AS ?relationType).
  
  BIND(IF(?ysoCinterface = ?ysoC2,
          concat("'",?ysoCpref, "' (yso:",substr(str(?ysoC),28) ,") käsite on deprekoitu uudessa YSOssa"),
          concat("'",?ysoCpref, "' (yso:",substr(str(?ysoC),28) ,") käsitteellä on yläkäsite '",?ysoC2pref, "' (yso:",substr(str(?ysoC2),28) ,"), joka on deprekoitu uudessa YSOssa")
          ) AS ?changeDescription)
  
  } 
}
}

*count-domainC-subClasses-as-nroSubConcepts*

}ORDER BY ?domainC
